mapper.select:
select dish.*, category.name as category_name from dish left outer join category on dish.category_id = category.id
and dish.category_id = #{categoryId}
and dish.name like concat('%', #{name}, '%')
and dish.status = #{status}
order by dish.create_time desc

mapper.delete:
delete from dish
where id in

    #{id}

mapper.update:
update dish
name = #{name}, category_id = #{categoryId}, price = #{price}, image = #{image}, description = #{description}, status = #{status}, update_time = #{updateTime}, update_user = #{updateUser},
where id = #{id}

mapper.select:
select *
from dish


        and name like concat('%', name, '%')


        and category_id = #{categoryId}


        and status = #{status}


order by create_time desc

select count(id) from dish


        and status = #{status}


        and category_id = #{categoryId}
