mapper.select:
select *
from orders


        and number like concat('%',#{number},'%')


        and phone like concat('%',#{phone},'%')


        and user_id = #{userId}


        and status = #{status}


        and order_time >= #{beginTime}


        and order_time <= #{endTime}


order by order_time desc

SELECT DATE(order_time) AS order_date, SUM(amount) AS total_amount
FROM orders
WHERE status = 5

    and DATE(order_time) >= #{begin}


    and DATE(order_time) <= #{end}

GROUP BY DATE(order_time);

select date(order_time) as order_date, count(id) as order_count
from orders


        and date(order_time) >= #{begin}


        and date(order_time) <= #{end}


group by date(order_time);

select date(order_time) as order_date, count(id) as valid_count
from orders
where status = 5

        and date(order_time) >= #{begin}


        and date(order_time) <= #{end}

group by date(order_time);

select od.name, sum(od.number) as number
from order_detail od left join orders o on od.order_id = o.id
where o.status = 5

    and o.order_time >= #{begin}


    and o.order_time <= #{end}

group by od.name
order by number desc
limit 0,10;
